const body = $input.first().json.body || {};
const entities = body.entities || [];

if (!entities.length) {
  return [{ json: { nodes: [], edges: [] } }];
}

// Build Cypher query to get related entities
const cypher = `
  UNWIND $entities AS entityName
  MATCH (e:Entity)
  WHERE e.name =~ ('(?i).*' + entityName + '.*')
  OPTIONAL MATCH (e)-[r]->(related:Entity)
  RETURN DISTINCT
    e.name as name,
    e.type as type,
    e.description as description,
    collect(DISTINCT {name: related.name, type: type(r)}) as related
  LIMIT 20
`;

return [{
  json: {
    entities: entities,
    cypher: cypher,
    params: { entities: entities }
  }
}];

const neo4jResponse = $input.first().json;
const results = neo4jResponse.results?.[0]?.data || [];

const nodes = [];
const edges = [];
const seenNodes = new Set();

results.forEach(r => {
  const row = r.row;
  const name = row[0];
  const type = row[1];
  const desc = row[2];
  const related = row[3] || [];

  if (name && !seenNodes.has(name)) {
    nodes.push({ name, type: type || 'Entity', description: desc || '' });
    seenNodes.add(name);
  }

  related.forEach(rel => {
    if (rel.name && !seenNodes.has(rel.name)) {
      nodes.push({ name: rel.name, type: 'Entity', description: '' });
      seenNodes.add(rel.name);
    }
    if (rel.name) {
      edges.push({ from: name, to: rel.name, type: rel.type || 'RELATED_TO' });
    }
  });
});

return [{ json: { nodes, edges } }];
