const body = $input.first().json.body || {};

const query = (body.query || body.question || '').trim();
if (!query || query.length === 0) {
  return [{ json: { error: true, message: 'Query is required' } }];
}

return [{
  json: {
    query: query.slice(0, 2000),
    hybrid_mode: body.hybrid_mode !== false,  // Default: true
    vector_limit: Math.min(body.vector_limit || 5, 10),
    graph_depth: Math.min(body.graph_depth || 2, 3),
    model: body.model || 'mistral:7b',
    error: false
  }
}];

const esResponse = $input.first().json;
const config = $('Validate Input').first().json;

const hits = esResponse.hits?.hits || [];
const vectorResults = hits.map(hit => ({
  id: hit._id,
  score: (hit._score - 1).toFixed(4),
  title: hit._source?.title || 'Untitled',
  content: hit._source?.content || '',
  source: hit._source?.source || 'unknown'
}));

// Build vector context
let vectorContext = '';
if (vectorResults.length > 0) {
  vectorContext = vectorResults.map((r, i) => 
    `[Document ${i+1}: ${r.title}]\n${r.content}`
  ).join('\n\n');
}

return [{
  json: {
    query: config.query,
    hybrid_mode: config.hybrid_mode,
    graph_depth: config.graph_depth,
    model: config.model,
    vector_results: vectorResults,
    vector_context: vectorContext,
    vector_count: vectorResults.length
  }
}];

