const ollamaResponse = $input.first().json.response || '[]';
const prevData = $('Process Vector Results').first().json;

// Parse entities from LLM response
let entities = [];
try {
  const match = ollamaResponse.match(/\[[^\]]*\]/);
  if (match) {
    entities = JSON.parse(match[0]);
  }
} catch (e) {
  // Try to extract quoted strings
  const quoted = ollamaResponse.match(/["']([^"']+)["']/g);
  if (quoted) {
    entities = quoted.map(s => s.replace(/["']/g, ''));
  }
}

entities = entities.filter(e => typeof e === 'string' && e.length > 1).slice(0, 5);

return [{
  json: {
    ...prevData,
    extracted_entities: entities
  }
}];

const data = $input.first().json;
const entities = data.extracted_entities || [];
const depth = data.graph_depth || 2;

if (entities.length === 0) {
  return [{ json: { ...data, graph_context: '', skip_graph: true } }];
}

// Build Cypher query to get context for all extracted entities
const cypher = `
  UNWIND $entities AS entityName
  MATCH (e:Entity)
  WHERE e.name =~ ('(?i).*' + entityName + '.*')
  OPTIONAL MATCH (e)-[r1]->(related:Entity)
  OPTIONAL MATCH (e)<-[r2]-(incoming:Entity)
  RETURN e.name as entity, e.type as type, e.description as description,
         collect(DISTINCT {name: related.name, type: related.type, rel: type(r1)})[0..5] as related,
         collect(DISTINCT {name: incoming.name, type: incoming.type, rel: type(r2)})[0..5] as referenced_by
  LIMIT 10
`;

return [{
  json: {
    ...data,
    cypher: cypher,
    cypher_params: { entities: entities },
    skip_graph: false
  }
}];

const neo4jResponse = $input.first().json;
const prevData = $('Build Graph Query').first().json;

const results = neo4jResponse.results?.[0]?.data || [];

let graphContext = '';
if (results.length > 0) {
  graphContext = '## Knowledge Graph Context\n\n';
  results.forEach(r => {
    const row = r.row;
    graphContext += `### ${row[0]} (${row[1]})\n`;
    if (row[2]) graphContext += `${row[2]}\n`;

    const related = (row[3] || []).filter(x => x.name);
    if (related.length > 0) {
      graphContext += 'Relationships:\n';
      related.forEach(rel => {
        graphContext += `- ${rel.rel} â†’ ${rel.name} (${rel.type})\n`;
      });
    }

    const refs = (row[4] || []).filter(x => x.name);
    if (refs.length > 0) {
      graphContext += 'Referenced by:\n';
      refs.forEach(ref => {
        graphContext += `- ${ref.name} (${ref.type}) via ${ref.rel}\n`;
      });
    }
    graphContext += '\n';
  });
}

return [{
  json: {
    query: prevData.query,
    model: prevData.model,
    vector_context: prevData.vector_context,
    vector_count: prevData.vector_count,
    graph_context: graphContext,
    extracted_entities: prevData.extracted_entities,
    hybrid_mode: true
  }
}];

// Pass through for vector-only mode
const data = $('Process Vector Results').first().json;
return [{
  json: {
    query: data.query,
    model: data.model,
    vector_context: data.vector_context,
    vector_count: data.vector_count,
    graph_context: '',
    extracted_entities: [],
    hybrid_mode: false
  }
}];

// No entities found, skip graph
const data = $('Build Graph Query').first().json;
return [{
  json: {
    query: data.query,
    model: data.model,
    vector_context: data.vector_context,
    vector_count: data.vector_count,
    graph_context: '',
    extracted_entities: [],
    hybrid_mode: true
  }
}];

const data = $input.first().json;

// Combine contexts
let fullContext = '';

if (data.graph_context) {
  fullContext += data.graph_context + '\n';
}

if (data.vector_context) {
  fullContext += '## Retrieved Documents\n\n' + data.vector_context;
}

if (!fullContext.trim()) {
  fullContext = 'No relevant context found.';
}

// Build prompt
const prompt = `You are a helpful assistant. Answer the question based on the provided context. If the context doesn't contain enough information, say so.

Context:
${fullContext}

Question: ${data.query}

Answer:`;

return [{
  json: {
    prompt: prompt,
    model: data.model,
    query: data.query,
    vector_count: data.vector_count,
    graph_context_used: !!data.graph_context,
    hybrid_mode: data.hybrid_mode,
    extracted_entities: data.extracted_entities
  }
}];

const llmResponse = $input.first().json;
const meta = $('Merge Context').first().json;

return [{
  json: {
    query: meta.query,
    answer: llmResponse.response || 'Failed to generate answer.',
    mode: meta.hybrid_mode ? 'hybrid' : 'vector_only',
    sources: {
      vector_documents: meta.vector_count,
      graph_context_used: meta.graph_context_used,
      entities_extracted: meta.extracted_entities || []
    }
  }
}];
