// Input validation and sanitization
const body = $input.first().json.body || {};

// Validate required fields
if (typeof body.content !== 'string' || body.content.trim().length === 0) {
  return [{ json: { error: true, message: 'Content is required and must be a non-empty string' } }];
}

// Sanitize and limit content (max 50KB)
const content = body.content.trim().slice(0, 50000);

// Sanitize title (max 200 chars, alphanumeric + spaces)
const title = (body.title || 'Untitled Document')
  .replace(/[^a-zA-Z0-9\s\-_]/g, '')
  .slice(0, 200) || 'Untitled';

// Sanitize source (max 500 chars)
const source = (body.source || 'n8n-upload')
  .replace(/[^a-zA-Z0-9\s\-_:\/\.]/g, '')
  .slice(0, 500);

return [{
  json: {
    content: content,
    title: title,
    source: source,
    timestamp: new Date().toISOString(),
    error: false
  }
}];

// Chunk text into smaller pieces for embedding
const content = $input.first().json.content;
const title = $input.first().json.title;
const source = $input.first().json.source;
const timestamp = $input.first().json.timestamp;

const chunkSize = 500;
const chunkOverlap = 50;
const chunks = [];
let start = 0;
let chunkIndex = 0;

// Generate safe chunk_id from title
const safeTitle = title.replace(/[^a-zA-Z0-9]/g, '_').slice(0, 50);

while (start < content.length) {
  const end = Math.min(start + chunkSize, content.length);
  const chunkText = content.slice(start, end);

  chunks.push({
    json: {
      chunk_id: `${safeTitle}_chunk_${chunkIndex}`,
      chunk_index: chunkIndex,
      content: chunkText,
      title: title,
      source: source,
      timestamp: timestamp,
      total_chunks: Math.ceil(content.length / (chunkSize - chunkOverlap))
    }
  });

  start += chunkSize - chunkOverlap;
  chunkIndex++;
}

return chunks;

